CREATE TABLE `si_log` (
`id` BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY ,
`timestamp` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ,
`userid` INT NOT NULL ,
`sqlquerie` TEXT NOT NULL
) ENGINE = MYISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE IF NOT EXISTS `si_auth_challenges` (
`challenges_key` int(11) NOT NULL,
`challenges_timestamp` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP);

CREATE TABLE si_user_role (
	    `id` int(11) NOT NULL auto_increment  PRIMARY KEY,
            `name` varchar(255) UNIQUE NOT NULL
            ) ENGINE=MYISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE `si_index` (
`id` INT( 11 ) NOT NULL ,
`node` VARCHAR( 255 ) NOT NULL ,
`sub_node` VARCHAR( 255 ) NULL ,
`domain_id` INT( 11 ) NOT NULL
) ENGINE = MYISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE `si_cron_log` (
            `id` INT( 11 ) NOT NULL AUTO_INCREMENT ,
            `domain_id` INT( 11 ) NOT NULL ,
            `run_date` DATE NOT NULL ,
            PRIMARY KEY (  `domain_id` ,  `id` )
            ) ENGINE = MYISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

ALTER TABLE `si_system_defaults` CHANGE `id` `id` INT(11) NOT NULL;
ALTER TABLE `si_system_defaults` DROP PRIMARY KEY;
ALTER TABLE `si_system_defaults` ADD PRIMARY KEY(`domain_id`,`id` );
ALTER TABLE `si_system_defaults` DROP INDEX `name`;
ALTER TABLE `si_system_defaults` CHANGE `id` `id` INT(11) NOT NULL AUTO_INCREMENT;

CREATE TABLE `si_products_attributes` (
    `id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY ,
    `name` VARCHAR( 255 ) NOT NULL,
    `type_id` VARCHAR( 255 ) NOT NULL
    ) ENGINE = MYISAM ;

CREATE TABLE `si_products_values` (
`id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY ,
`attribute_id` INT( 11 ) NOT NULL ,
`value` VARCHAR( 255 ) NOT NULL
) ENGINE = MYISAM ;

CREATE TABLE `si_products_attribute_type` (
            `id` int(11) NOT NULL AUTO_INCREMENT,
                `name` varchar(255) NOT NULL,
                  PRIMARY KEY (`id`)
              ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

